<?php		if(!empty($_POST))	{		$msg="";				if(empty($_POST['fnm']) || empty($_POST['unm'])  || empty($_POST['pwd']) || empty($_POST['cpwd']) || empty($_POST['mail'])||empty($_POST['city']))		{			$msg.="<li>Please full fill all requirement";		}				if($_POST['pwd']!=$_POST['cpwd'])		{			$msg.="<li>Please Enter your Password Again.....";		}										if(strlen($_POST['pwd'])>10)		{			$msg.="<li>Please Enter Your Password in limited Format....";		}				if(is_numeric($_POST['fnm']))		{			$msg.="<li>Name must be in String Format...";		}				if($msg!="")		{			header("location:login.php?error=".$msg);		}		else		{			$fnm=$_POST['fnm'];			$unm=$_POST['unm'];			$pwd=$_POST['pwd'];						$email=$_POST['mail'];			$contact=$_POST['contact'];			$city=$_POST['city'];											$link = mysqli_connect("localhost","root","","final_shop") or die("error");												$sql="insert into user(u_fnm,u_unm,u_pwd,u_email,u_contact,u_city)			values('$fnm','$unm','$pwd','$email','$contact','$city')";						$query = mysqli_query($link, $sql);         if(!$query){             echo 'Error'. mysqli_error($link);         } else{             mysqli_close($link);             header('location:process_login.php');		 }		}	}	else	{		header("location:index.php");	}?>